<script>
import db from '../firebase'
import { Carousel, Slide } from 'vue-carousel';

export default {
  components: {
    Carousel,
    Slide
  },
  data () {
    return {
      title:"my vue file",
      propertyInDemand:[],
      num:2,
      next:"next"
    }
  },
  methods:{
     goto:function(){
           let queryObject = {
                // type: this.typeinput,
                location: "anywhere",
                type: 'private rooms',
                // ForWhom: this.forwhomselected,
            }
        //   console.log(queryObject);  
          this.$router.push({name:'result',query:{queryObject}});
       }
  },
  created(){
    db.collection('properties').get().then((res) =>{ 
        res.docs.forEach( (doc)=> {
          this.propertyInDemand.push(doc.data());
        })
    }) 
    if(window.innerWidth>480){
      this.num=3;
    }
  },
  mounted(){
          this.$root.$children[0].$children[0].$el.style.display="block"; // to show nav bar
    window.onresize = () => {
                 
                 if(window.innerWidth<=480){
                   this.num=2;
                 }
                 else{
                   this.num=3;
                 }
            }
  }
}
</script>


